class MarvelService {
  getResource = async(url) => {
    let res = await fetch(url);

    if (!res.ok) {
      throw new Error(`Could not fetch ${url}, status: ${res.status}`);
    }

    return await res.json();
  }

  getAllCharacters = () => {
    return this.getResource('https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=d16d8868d4036e3d39fdc98f82ffe14b');
  }

  getCharacter = (id) => {
    return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=d16d8868d4036e3d39fdc98f82ffe14b`);
  }
}

export default MarvelService;
